import React, { useState } from 'react';
import cities from './cities';
import API_KEY from './config';

function WeatherApp() {
  const [city, setCity] = useState('');
  const [weatherInfo, setWeatherInfo] = useState(null);
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const apiKey = API_KEY; // Use API key from config.js
  const apiUrl = 'https://api.openweathermap.org/data/2.5/weather';

  const getWeather = async () => {
    if (city.trim() === '') {
      setMessage('Please enter a city name.');
      setWeatherInfo(null);
      return;
    }
    if (!cities.includes(city)) {
      setMessage('City not found');
      setWeatherInfo(null);
      return;
    }
    setLoading(true);
    setMessage('');
    setWeatherInfo(null);
    try {
      const response = await fetch(apiUrl + '?q=' + city + ',IN&appid=' + apiKey + '&units=metric');
      if (!response.ok) {
        throw new Error('City not found');
      }
      const data = await response.json();
      setWeatherInfo({
        temp: data.main.temp,
        description: data.weather[0].description,
        city: data.name,
      });
      setMessage('');
    } catch (error) {
      if (error.message === 'Failed to fetch') {
        setMessage('Network error or CORS issue. Please check your API key and network.');
      } else {
        setMessage(error.message);
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Weather App</h1>
      <input
        type="text"
        placeholder="Enter City Here"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ padding: '8px', fontSize: '16px' }}
        list="citiesList"
      />
      <datalist id="citiesList">
      {cities.map((cityName) => (
          <option key={cityName} value={cityName} />
        ))}
      </datalist>
      <button
        onClick={getWeather}
        style={{ marginLeft: '10px', padding: '8px 16px', fontSize: '16px' }}
        disabled={loading}
      >
        {loading ? 'Loading...' : 'Click here to Get Weather'}
      </button>
      <div style={{ marginTop: '20px', fontSize: '18px' }}>
        {message && <p>{message}</p>}
        {weatherInfo && (
          <p>
            The weather in {weatherInfo.city} is {weatherInfo.temp}Â°C with {weatherInfo.description}.
          </p>
        )}
      </div>
    </div>
  );
}

export default WeatherApp;
