import React, { useState } from 'react';

const weatherData = {
  Ahmedabad: 40,
  Mumbai: 32,
  Delhi: 38,
  Bangalore: 28,
  Chennai: 35,
};

function WeatherApp() {
  const [city, setCity] = useState('');
  const [temperature, setTemperature] = useState(null);
  const [message, setMessage] = useState('');

  const getWeather = () => {
    if (city.trim() === '') {
      setMessage('Please enter a city name.');
      setTemperature(null);
      return;
    }
    const temp = weatherData[city];
    if (temp !== undefined) {
      setTemperature(temp);
      setMessage(`The weather in ${city} is ${temp}°C`);
    } else {
      setTemperature(null);
      setMessage(`Weather data for "${city}" is not available.`);
    }
  };

  return (
    <div style={{ textAlign: 'center', marginTop: '50px' }}>
      <h1>Weather App</h1>
      <input
        type="text"
        placeholder="Enter City Here"
        value={city}
        onChange={(e) => setCity(e.target.value)}
        style={{ padding: '8px', fontSize: '16px' }}
      />
      <button
        onClick={getWeather}
        style={{ marginLeft: '10px', padding: '8px 16px', fontSize: '16px' }}
      >
        Click here to Get Weather
      </button>
      <div style={{ marginTop: '20px', fontSize: '18px' }}>
        {message}
      </div>
    </div>
  );
}

export default WeatherApp;
